package com.syntax.class07;

public class WhilevsDoWhile {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
// hello 5 times
		
		int num=11;
		
		while (num<=5) {
			
			System.out.println("hello");
			num++;
			
		}
		
		System.out.println("------do while loop-------");
		
		
		int num1=1;
		
		do {
			System.out.println("hello");
			num1++;
			
		} while(num1<=5);
		
	
	
	
	}

}
